angular.module("ALAuth", ['firebase','ngCookies'])
  .directive('header', function() {
    return {
      restrict: 'E',
      controller: ['$rootScope', '$scope', '$firebase', '$cookies',
       function($rootScope, $scope, $firebase, $cookies) {
        var rootRef = new Firebase($cookies.rootRef);
        var usersRef = rootRef.child('users');
        var welcomeRef = rootRef.child('welcome');

        // Start with no user logged in
        $rootScope.currentUser = null;

        // Upon successful login, set the user object
        // happens automatically when rememberMe is enabled
        rootRef.onAuth(function(authData) {
          if(authData) {

            // todo: this is a brute force redirect after login
            //       How can we do it within routing framework?
            if(window.location.pathname === "/") {
              window.location.pathname = "/journal";
            }

            console.log("auth: user is logged in");
            var user = buildAhhLifeUserObjectFromGoogle(authData);

            // site presence
            user.active = true;

            var userRef = usersRef.child(user.uid);
            userRef.on('value', function(snap){
              if(snap.val()) {
                userRef.child('active').set(true);
              }
              else {
                user.created = Firebase.ServerValue.TIMESTAMP;
                userRef.set(user);
                welcomeRef.child(user.uid).set(user);
                analytics.track('Signup');
              }
              // set user to inactive on disconnect
              userRef.child('active').onDisconnect().set(false);
            });

            $rootScope.currentUser = user;

            analytics.identify(user.uid, {
              name: user.name,
              firstName: user.firstName,
              lastName: user.lastName,
              email: user.email
            });
          }
          else {
            // user is logged out
            console.log("auth: user is logged out");
            $rootScope.currentUser = null;
          }
        });

        $scope.login = function() {
          analytics.track('Login');
          options = { remember: true, scope: "email" };
          rootRef.authWithOAuthRedirect("google", function(err, authData) {
            if(err) {
              console.log('error logging in', err);
            } else {
              console.log('login successful');
            }
          }, options);
        }

        $scope.logout = function() {
          rootRef.unauth();
          window.location.pathname = "/";
        };
      }]
    }
  });

function buildAhhLifeUserObjectFromGoogle(authData) {
  return {
    uid: authData.uid,
    name: authData.google.displayName,
    email: authData.google.email,
    accessToken: authData.google.accessToken,
    firstName: authData.google.cachedUserProfile.given_name,
    lastName: authData.google.cachedUserProfile.family_name,
    picture: authData.google.cachedUserProfile.picture
  }
}
angular.module("ALJournalService", ['firebase','ngCookies'])
 .factory('JournalService', ['$rootScope', '$firebase', '$cookies',
   function($rootScope, $firebase, $cookies) {
    var rootRef = new Firebase($cookies.rootRef);
    var journalsRef = rootRef.child('journals');
    var settingsRef = rootRef.child('settings');
    var usersRef = rootRef.child('users');

    var service = {
      // initialized on login below
      draft: null,
      months: null,
      user: null,

      deleteEntry: function(entries, entry, cb) {
        usersRef.child(this.user.uid).child('entries').child(entry.$id).remove();
        return entries.$remove(entry);
      },

      deleteAccount: function() {
        analytics.track('Account Deletion');
        journalsRef.child(this.user.uid).remove();
        settingsRef.child(this.user.uid).remove();
        usersRef.child(this.user.uid).remove(function() {
          rootRef.unauth();
          window.location.pathname = "/";
        });
      },

      getMonthEntries: function(id) {
        return $firebase(journalsRef.child(this.user.uid).child('months').child(id)).$asArray();
      },

      save: function() {
        this.draft.timestamp = Firebase.ServerValue.TIMESTAMP;
        var currentMonthKey = moment().format("MMM-YYYY")
        var currentMonth = $firebase(journalsRef.child(this.user.uid).child('months').child(currentMonthKey)).$asArray();
        var self = this;
        return currentMonth.$add(this.draft).then(function(ref){
          // the entries index on user record allows us to pull random entries later
          usersRef.child(self.user.uid).child('entries').child(ref.name()).set(ref.toString());
          // clear the draft
          $firebase(journalsRef.child(self.user.uid).child('draft/content')).$set("");
        });
      }
    }

    rootRef.onAuth(function(authData) {
      if(authData) {
        console.log("journal-service: user is logged in");
        service.user = authData;

        $firebase(journalsRef.child(authData.uid).child('draft')).$asObject().$loaded().then(function(draft){
          service.draft = draft;
          $rootScope.$broadcast("AhhLife:draftLoaded", draft);
        });

        $firebase(journalsRef.child(authData.uid).child('months')).$asArray().$loaded().then(function(months){
          service.months = months;
          $rootScope.$broadcast("AhhLife:monthsLoaded", months);
        });

        $firebase(settingsRef.child(authData.uid)).$asObject().$loaded().then(function(settings){
          $rootScope.$broadcast("AhhLife:settingsLoaded", settings);
        });
      }
      else {
        console.log("journal-service: user is logged out");
        service.draft = null;
        service.months = null;
        service.user = null;
      }
    });

    return service;
  }]);
angular.module("ReverseFilter",[])
 .filter('reverse', function() {
  function toArray(list) {
    var k, out = [];
    if( list ) {
      if( angular.isArray(list) ) {
        out = list;
      }
      else if( typeof(list) === 'object' ) {
        for (k in list) {
          if (angular.isObject(list[k])) { out.push(list[k]); }
        }
      }
    }
    return out;
  }
  return function(items) {
    return toArray(items).slice().reverse();
  };
});
module.exports = "<h1>My Journal</h1><div ng-if=\"currentUser &amp;&amp; !_.any(months)\" class=\"row\"><div class=\"col-md-5\"><h2>Time to check your inbox</h2><p>We'll send you a similar message each evening. All you have to do is reply to it and whatever you send will be added to your journal automatically.</p><p>If you would rather update your journal automatically, you can also use the WRITE link at the top of the screen.</p></div><div class=\"col-md-7\"><div style=\"margin-top: 20px\" class=\"email\"><p>From: AhhLife</p><p>To: me</p><p>Subject: Thanks for signing up</p><hr/><p>Just reply to this email with your entry.</p><p>Write about anything from today - like what you did,  something you're feeling, or a conversation you had with a friend. And feel free to keep it short. A quick sentence is just fine! But if you want to write more, you can go ahead and write a longer entry too.</p><p>After you reply to this email, your entry will show up here: http://ahhlife.com/journal</p></div></div></div><div ng-if=\"_.any(entries)\" class=\"row\"><div class=\"col-md-9 col-md-offset-1\"><div ng-repeat=\"(idx, entry) in entries | reverse\" class=\"entry material-whiteframe-z1\"><div layout=\"horizontal\" class=\"heading\"><h2>{{ moment(entry.timestamp).format(\"MMMM Do YYYY\") }}</h2><div class=\"entry-actions\"><a ng-click=\"deleteEntry(entries, entry)\" title=\"Delete this entry permanently.\"><i class=\"fa fa-trash\"></i></a></div></div><h3>{{ moment(entry.timestamp).format(\"dddd\") }}</h3><div class=\"text\"><p>{{ entry.content }}</p></div></div></div></div><div ng-if=\"_.any(months) &amp;&amp; !_.any(entries)\" class=\"row\"><div class=\"col-md-10 col-md-offset-1\"><div class=\"entry material-whiteframe-z1\"><h3>No month selected</h3></div></div></div>";
module.exports = "<h1>Write</h1><div ng-controller=\"WriteController\" class=\"row\"><div class=\"col-md-9\"><form class=\"new-entry\"><textarea ng-model=\"draft.content\" ng-model-options=\"{ updateOn: 'default blur', debounce: {'default': 500, 'blur': 0} }\" class=\"material-whiteframe-z3\"></textarea><br/><material-button ng-click=\"save()\" class=\"material-button-raised\">Save to my Journal</material-button></form></div><div class=\"col-md-3\"><h3 class=\"text-center\">Tips</h3><p>The easiest way to write entries is by replying to the emails we send you.</p><hr/><p>Any text you enter here is automatically saved as a draft in case you want to write a little then come back later.</p></div></div>";
module.exports = "<h1>Settings</h1><div class=\"row\"><div class=\"col-md-7\"><material-content id=\"settings\"><section><h3>What time should we email you every day?</h3><select ng-model=\"settings.timeslot\"><option ng-repeat=\"hour in slots\" value=\"{{hour.utc().format('H')}}\">{{ hour.local().format('h:mm a') }}</option></select></section><section><h3>Should the email include a past entry?</h3><select ng-model=\"settings.includePast\"><option value=\"true\">Yes</option><option value=\"false\">No</option></select></section><section><material-button ng-click=\"saveSettings(settings.timeslot)\" class=\"material-button-raised material-button-colored\">Save</material-button></section></material-content></div><div class=\"col-md-5\"><h3>Account Deletion</h3><p>Warning: deleting your account will destroy all journal entries and log you out. There is <strong>no undo</strong> and you will not be able to recover your entries.</p><p><material-button ng-click=\"deleteAccount()\" class=\"material-button-raised material-theme-red\">I understand, DELETE MY ACCOUNT</material-button></p></div></div>";
module.exports = "<div class=\"backdrop material-whiteframe-z3\"><h1 class=\"h1\">FAQ</h1><p class=\"subtext\">(Frequently Asked Questions)</p><h2 class=\"h3\">Is this just a blatant copy of OhLife?</h2><p>It's the same basic idea as OhLife, but on a modern tech platform that we think is a lot more cost-efficient and easy to maintain than how (we suspect) OhLife was put together. And there is absolutely no venture capital or investor backing for this project, so there is no pressure to make it hugely profitable. That means we don't have to worry about <a href=\"https://ohlife.com/shutdown\" target=\"_new\">suddenly shutting down</a> the way that OhLife did.</p><h2 class=\"h3\">Is there a mobile app?</h2><p>No and we don't anticipate a need for one in the future. This website is optimized to work on mobile phones, and you should be using it via email anyway.</p><h2 class=\"h3\">Can I add an entry via your website?</h2><p>Yep, login and click on WRITE to get a new entry form. Drafts are automatically saved for you.</p><h2 class=\"h3\">Can I edit or delete an old entry via the website?</h2><p>Editing old entries is coming soon. You can delete an entry in your journal using the trash icon. Note: deletion of entries is permanent.</p><h2 class=\"h3\">Can I import my old OhLife entries?</h2><p>That feature is coming soon.</p><h2 class=\"h3\">I don't have a Google Account (or don't want to use mine) to log in to your site. Can I use something else?</h2><p>Sorry, at the moment we only work in conjunction with a Google account. This dramatically simplifies the way that the site is written and helps us with security.</p><h2 class=\"h3\">I'm new and didn't get my email tonight. How come?</h2><p>Depending on what time you signed up, things sometimes work differently on your first day. We always send you a welcome email right when you sign up. After your first day, you'll always get your email at night based on your <a href=\"/journal/settings\">settings</a>.</p><h2 class=\"h3\">Can I reply to an email more than once?</h2><p>Yes, reply as many times as you want and an entry will be added for each message you send us.</p><h2 class=\"h3\">Why is my formatting gone?</h2><p>Plain text entries will always be free. However, we plan to offer the ability to send formatted email (meaning different colors and fonts, etc.) as a premium feature. We promise it won't cost too much, and we'll let you know when that feature is ready.</p><h2 class=\"h3\">Can I attach photos/documents to my entry? Where did my attachment go?</h2><p>Right now we only support plain-text journal entries. We're planning to add the ability to attach photos and other documents to your journal entries via email as a premium feature in a future release.</p><h2 class=\"h3\">How do I download my journal?</h2><p>Right now we haven't built the export feature yet, but we plan to do so soon.</p><h2 class=\"h3\">How do I delete my account.</h2><p>There is a red button in Account Settings that will let you delete your account and wipe all of your data from our server.</p><h2 class=\"h3\">Who runs this thing? Why should I trust you?</h2><p>AhhLife is built by the same team that runs <a href=\"http://dueprops.com\" target=\"_new\">DueProps</a>, amongst other things. Right now we're doing this as a labor of love, because it doesn't really cost that much. However, we have plans for modestly priced premium accounts that will allow rich-text entries and photo attachments. We think the revenue from those plans will defray server costs easily.</p><h2 class=\"h3\">Any more Questions?</h2><p>Please send us an email at ahhlife.team@gmail.com. We're happy to help out and answer any of your questions.</p></div>";
module.exports = "<div class=\"backdrop material-whiteframe-z3\"><h1 class=\"h1\">Privacy Policy</h1><p class=\"subtext\">We take your privacy very seriously</p><p>We believe in protecting your information the same way we want ours protected. This Privacy Policy outlines how we treat your information.</p><h2>Private Entries</h2><p>Your journal entries are private. The only way you can access them is by logging into your account, or by our nightly emails that are sent to your email address only.</p><p>There's no way to share your entries. We don't have any integration with Twitter or Facebook, there's no way to make an entry public, and your entries are not searchable by Google. OhLife is strictly a private place for you to read and write about how your days went.</p><h2>Information Collected</h2><p>We only collect information needed in order for you to use the service (basically just your name and email, which we get from Google OAuth), plus a small amount of activity data that lets us analyze and improve the way the site works (via segment.io.) Your journal entries are never shared with or sold to any third parties.</p><h2>Test Run</h2><p>If you're unhappy with the service for any reason, you can delete your account in account settings. This will permanently delete your account and all of your entries from our server.</p><h2>Privacy Policy Changes</h2><p>We may make changes to our Privacy Policy at some point, but probably not. Just in case we do, you should know that the new Privacy Policy will be posted on this page and you will receive an email letting you know about the change.</p><h2>Questions?</h2><p>Please send us an email at ahhlife.team@gmail.com. We're happy to help out and answer any of your questions.</p></div>";
